describe DocsController do

  # This should return the minimal set of attributes required to create a valid
  # Doc. As you add validations to Doc, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    { "title" => "MyString" }
  end

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # DocsController. Be sure to keep this updated too.
  def valid_session
    {}
  end

  describe "GET index" do
    it "assigns all docs as @docs" do
      doc = Doc.create! valid_attributes
      get :index, {}, valid_session
      assigns(:docs).should eq([doc])
    end
  end

  describe "GET show" do
    it "assigns the requested doc as @doc" do
      doc = Doc.create! valid_attributes
      get :show, {:id => doc.to_param}, valid_session
      assigns(:doc).should eq(doc)
    end
  end

  describe "GET new" do
    it "assigns a new doc as @doc" do
      get :new, {}, valid_session
      assigns(:doc).should be_a_new(Doc)
    end
  end

  describe "GET edit" do
    it "assigns the requested doc as @doc" do
      doc = Doc.create! valid_attributes
      get :edit, {:id => doc.to_param}, valid_session
      assigns(:doc).should eq(doc)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Doc" do
        expect {
          post :create, {:doc => valid_attributes}, valid_session
        }.to change(Doc, :count).by(1)
      end

      it "assigns a newly created doc as @doc" do
        post :create, {:doc => valid_attributes}, valid_session
        assigns(:doc).should be_a(Doc)
        assigns(:doc).should be_persisted
      end

      it "redirects to the created doc" do
        post :create, {:doc => valid_attributes}, valid_session
        response.should redirect_to(Doc.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved doc as @doc" do
        # Trigger the behavior that occurs when invalid params are submitted
        Doc.any_instance.stub(:save).and_return(false)
        post :create, {:doc => { "title" => "invalid value" }}, valid_session
        assigns(:doc).should be_a_new(Doc)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Doc.any_instance.stub(:save).and_return(false)
        post :create, {:doc => { "title" => "invalid value" }}, valid_session
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested doc" do
        doc = Doc.create! valid_attributes
        # Assuming there are no other docs in the database, this
        # specifies that the Doc created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Doc.any_instance.should_receive(:update_attributes).with({ "title" => "MyString" })
        put :update, {:id => doc.to_param, :doc => { "title" => "MyString" }}, valid_session
      end

      it "assigns the requested doc as @doc" do
        doc = Doc.create! valid_attributes
        put :update, {:id => doc.to_param, :doc => valid_attributes}, valid_session
        assigns(:doc).should eq(doc)
