      it "redirects to the doc" do
        doc = Doc.create! valid_attributes
        put :update, {:id => doc.to_param, :doc => valid_attributes}, valid_session
        response.should redirect_to(doc)
      end
    end

    describe "with invalid params" do
      it "assigns the doc as @doc" do
        doc = Doc.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        Doc.any_instance.stub(:save).and_return(false)
        put :update, {:id => doc.to_param, :doc => { "title" => "invalid value" }}, valid_session
        assigns(:doc).should eq(doc)
      end

      it "re-renders the 'edit' template" do
        doc = Doc.create! valid_attributes
