    end
  end

  describe "DELETE destroy" do
    it "destroys the requested doc" do
      doc = Doc.create! valid_attributes
      expect {
        delete :destroy, {:id => doc.to_param}, valid_session
      }.to change(Doc, :count).by(-1)
